import {
  TrendingUp,
  Security,
  Warning,
  CheckCircle,
  Add
} from '@mui/icons-material';

interface DashboardStats {
  total_transactions: number;
  fraud_transactions: number;
  pending_alerts: number;
  fraud_rate: number;
}

interface Transaction {
  id: number;
  amount: number;
  user_id: string;
  merchant_id: string;
  is_fraud: boolean;
  fraud_score: number;
  timestamp: string;
  transaction_type: string;
}

const App: React.FC = () => {
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [recentTransactions, setRecentTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [newTransactionOpen, setNewTransactionOpen] = useState(false);
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    user_id: '',
    merchant_id: '',
    transaction_type: 'credit_card'
  });

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    fetchDashboardData();
    const interval = setInterval(fetchDashboardData, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [statsResponse, transactionsResponse] = await Promise.all([
        fetch(`${API_BASE}/api/dashboard/stats`),
        fetch(`${API_BASE}/api/transactions/?per_page=10`)
      ]);
      
      const statsData = await statsResponse.json();
      const transactionsData = await transactionsResponse.json();
      
      setStats(statsData);
      setRecentTransactions(transactionsData.transactions || []);
      setLoading(false);
      setError(null);
    } catch (err) {
      setError('Failed to connect to backend. Make sure the server is running on port 5000.');
      setLoading(false);
    }
  };

  const generateSampleData = async () => {
    try {
      setLoading(true);
      await fetch(`${API_BASE}/api/transactions/sample`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ count: 20 })
      });
      await fetchDashboardData();
    } catch (err) {
      setError('Failed to generate sample data');
      setLoading(false);
    }
  };

  const createTransaction = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/transactions/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: parseFloat(newTransaction.amount),
          user_id: newTransaction.user_id,
          merchant_id: newTransaction.merchant_id,
          transaction_type: newTransaction.transaction_type
        })
      });
      
      if (response.ok) {
        setNewTransactionOpen(false);
        setNewTransaction({ amount: '', user_id: '', merchant_id: '', transaction_type: 'credit_card' });
        await fetchDashboardData();
      }
    } catch (err) {
      setError('Failed to create transaction');
    }
  };

  if (loading && !stats) {
    return (
      <Container maxWidth="lg" style={{ marginTop: 64 }}>
        <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" minHeight="400px">
          <CircularProgress size={60} />
          <Typography variant="h6" style={{ marginTop: 16 }}>
            Loading Fraud Detection Dashboard...
          </Typography>
        </Box>
      </Container>
    );
  }

  const fraudRateColor = stats && stats.fraud_rate > 10 ? 'error' : stats && stats.fraud_rate > 5 ? 'warning' : 'success';

  return (
    <div>
      {/* Header */}
      <AppBar position="fixed">
        <Toolbar>
          <Typography variant="h6" component="div" style={{ flexGrow: 1 }}>
            🛡️ Fraud Detection Dashboard - Hackathon 2025
          </Typography>
          <Button color="inherit" onClick={() => setNewTransactionOpen(true)}>
            <Add style={{ marginRight: 8 }} />
            New Transaction
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" style={{ marginTop: 80, marginBottom: 32 }}>
        {error && (
          <Alert severity="error" style={{ marginBottom: 16 }}>
            {error}
            <Button onClick={fetchDashboardData} style={{ marginLeft: 16 }}>
              Retry
            </Button>
          </Alert>
        )}

        {/* Action Buttons */}
        <Box marginBottom={4}>
          <Button 
            variant="contained" 
            onClick={generateSampleData}
            disabled={loading}
            style={{ marginRight: 16 }}
          >
            Generate Sample Data
          </Button>
          <Button 
            variant="outlined"
            onClick={fetchDashboardData}
            disabled={loading}
          >
            {loading ? 'Refreshing...' : 'Refresh Data'}
          </Button>
        </Box>

        {/* Stats Cards */}
        <Grid container spacing={3} style={{ marginBottom: 32 }}>
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center">
                  <TrendingUp color="primary" style={{ marginRight: 16, fontSize: 40 }} />
                  <Box>
                    <Typography variant="h4">{stats?.total_transactions || 0}</Typography>
                    <Typography color="textSecondary">Total Transactions</Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center">
                  <Warning color="error" style={{ marginRight: 16, fontSize: 40 }} />
                  <Box>
                    <Typography variant="h4">{stats?.fraud_transactions || 0}</Typography>
                    <Typography color="textSecondary">Fraud Detected</Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center">
                  <Security color="warning" style={{ marginRight: 16, fontSize: 40 }} />
                  <Box>
                    <Typography variant="h4">{stats?.pending_alerts || 0}</Typography>
                    <Typography color="textSecondary">Pending Alerts</Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center">
                  <CheckCircle color={fraudRateColor} style={{ marginRight: 16, fontSize: 40 }} />
                  <Box>
                    <Typography variant="h4">{stats?.fraud_rate || 0}%</Typography>
                    <Typography color="textSecondary">Fraud Rate</Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* Recent Transactions */}
        <Paper style={{ padding: 24 }}>
          <Typography variant="h5" gutterBottom>
            Recent Transactions
          </Typography>
          
          {recentTransactions.length === 0 ? (
            <Alert severity="info">
              No transactions found. Generate some sample data to get started!
            </Alert>
          ) : (
            <Box>
              {recentTransactions.map((transaction) => (
                <Card key={transaction.id} style={{ marginBottom: 16 }}>
                  <CardContent>
                    <Grid container spacing={2} alignItems="center">
                      <Grid item xs={12} sm={3}>
                        <Typography variant="h6">
                          ${transaction.amount.toFixed(2)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {transaction.transaction_type}
                        </Typography>
                      </Grid>
                      
                      <Grid item xs={12} sm={3}>
                        <Typography variant="body1">
                          {transaction.user_id}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          User ID
                        </Typography>
                      </Grid>
                      
                      <Grid item xs={12} sm={3}>
                        <Chip 
                          label={transaction.is_fraud ? 'FRAUD' : 'LEGITIMATE'}
                          color={transaction.is_fraud ? 'error' : 'success'}
                          variant={transaction.is_fraud ? 'filled' : 'outlined'}
                          className={transaction.is_fraud ? 'fraud-alert' : ''}
                        />
                        <Typography variant="body2" color="textSecondary">
                          Score: {(transaction.fraud_score * 100).toFixed(1)}%
                        </Typography>
                      </Grid>
                      
                      <Grid item xs={12} sm={3}>
                        <Typography variant="body2" color="textSecondary">
                          {new Date(transaction.timestamp).toLocaleString()}
                        </Typography>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              ))}
            </Box>
          )}
        </Paper>

        {/* New Transaction Dialog */}
        <Dialog open={newTransactionOpen} onClose={() => setNewTransactionOpen(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Create New Transaction</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              label="Amount"
              type="number"
              fullWidth
              variant="outlined"
              value={newTransaction.amount}
              onChange={(e) => setNewTransaction({ ...newTransaction, amount: e.target.value })}
              style={{ marginBottom: 16 }}
            />
            <TextField
              margin="dense"
              label="User ID"
              fullWidth
              variant="outlined"
              value={newTransaction.user_id}
              onChange={(e) => setNewTransaction({ ...newTransaction, user_id: e.target.value })}
              style={{ marginBottom: 16 }}
            />
            <TextField
              margin="dense"
              label="Merchant ID"
              fullWidth
              variant="outlined"
              value={newTransaction.merchant_id}
              onChange={(e) => setNewTransaction({ ...newTransaction, merchant_id: e.target.value })}
              style={{ marginBottom: 16 }}
            />
            <TextField
              select
              margin="dense"
              label="Transaction Type"
              fullWidth
              variant="outlined"
              value={newTransaction.transaction_type}
              onChange={(e) => setNewTransaction({ ...newTransaction, transaction_type: e.target.value })}
              SelectProps={{
                native: true,
              }}
            >
              <option value="credit_card">Credit Card</option>
              <option value="upi">UPI</option>
              <option value="bank_transfer">Bank Transfer</option>
            </TextField>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setNewTransactionOpen(false)}>Cancel</Button>
            <Button onClick={createTransaction} variant="contained">Create Transaction</Button>
          </DialogActions>
        </Dialog>
      </Container>
    </div>
  );
};

export default App;
